$(function(){$('a[href="#search"]').on("click",function(n){n.preventDefault();$("#search").addClass("open");$('#search > form > input[type="search"]').focus()});$("#search, #search button.close").on("click keyup",function(n){(n.target==this||n.target.className=="close"||n.keyCode==27)&&$(this).removeClass("open")})});