$(function(){$(".ship-to").click(function(n){var t,i,r;n.preventDefault();t=$(this).attr("data-reference");$(this).html("Processing shipping..");$("#shipping_"+t).html('<p class="alert alert-default"><b><i>please confirm customer shipping details:<i><b><\/p><p class="well well_'+t+'"><\/p><p><\/p>');$.ajax({type:"GET",url:"../Group/GetShippingDetails?orderReference="+t}).done(function(n){n.error?alert(n.error):$(".well_"+t).html(n.user)}).fail(function(n){alert("unable to proceed");console.log(n)});i=$('<button class="proceed-ship" data-ship="'+t+' ">Proceed<\/button>').click(function(){var n=$(this).attr("data-ship")});r=$('<button class="cancel-ship" data-cancel="'+t+' ">cancel<\/button>').click(function(){var n=$(this).attr("data-cancel");$("#shipping_"+n).html(" ");$(".ship_"+n).html('ship order to customer <i class="ion ion-chevron-right"><\/i>')});$("#shipping_"+t).find("p:last").append(i);$("#shipping_"+t).find("p:last").append(r)})});