$(function(){$("form[name=add-to-cart]").validate({rules:{size:{required:!0},quantity:{required:!0,digits:!0}},messages:{size:{required:"Select a required size please"},quantity:{required:"Enter quantity please",digits:"Numbers only please"}},submitHandler:function(n){$.ajax({url:"/group/addToCart",type:"POST",data:$(n).serialize()}).done(function(n){alert("added to cart. cartId: "+n.cartItem.cartId)}).fail(function(n){alert("unable to add to cart");console.log(n)})}})});