"use strict";$(function(){$(".checkout").click(function(){$(".checkout").html("please wait...");$.ajax({url:"/group/getCart",type:"POST"}).done(function(n){n.noOfItems==0?alert("no items in your cart"):$.ajax({url:"../api/orders?cartId="+n.cartId,type:"POST"}).done(function(n){$(".checkout").html("Checkout");$(location).attr("href",n.url)}).fail(function(n){alert(n)})}).fail(function(n){console.log(n)})});$(".remove-item-cart").click(function(n){n.preventDefault();var t=$(this).attr("data-id");$(this).html("removing...");$.ajax({url:"../api/SelectedItems/"+t,type:"DELETE"}).done(function(){$("#item_"+t).fadeOut();$.ajax({url:"../group/getTotal",type:"GET"}).done(function(n){$(".cart_total_cost").html(n.total+" [NGN]");$(".count-message").html("You have "+n.noOfItems+" item(s) in your shopping cart");$("#cart_count").html(n.noOfItems);$("#cart_count_2").html(n.noOfItems)}).fail(function(n){console.log(n)})})})});